        stringBuilder.append("                    \"id\": 1555,\n");
        stringBuilder.append("                    \"locale\": \"en-us\",\n");
        stringBuilder.append("                    \"translation\": \"Getting more out of the Kayako search bar\",\n");
        stringBuilder.append("                    \"createdAt\": \"2016-06-24T22:37:23);00:00\",\n");
        stringBuilder.append("                    \"updatedAt\": \"2016-06-24T22:37:23);00:00\",\n");
