        stringBuilder.append("            \"slugs\": [\n");
        stringBuilder.append("                {\n");
        stringBuilder.append("                    \"locale\": \"en-us\",\n");
        stringBuilder.append("                    \"translation\": \"1164-getting-more-out-of-the-kayako-search-bar\",\n");
        stringBuilder.append("                    \"resourceType\": \"slug\"\n");
